import React from "react";
import pic from "../components/images/pic.jpg";
import picc from "../components/images/picc.jpg";
import piccc from "../components/images/piccc.jpg";

class Carousel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        {
          id: 0,
          title: "Portrait",
          subTitle: "hi",
          imgSrc: pic,
          link: "https://en.wikipedia.org/wiki/Chestnut",
          selected: false,
        },
        {
          id: 1,
          title: "Life",
          subTitle: "hi1",
          imgSrc: picc,
          link: "https://en.wikipedia.org/wiki/Chocolate",
          selected: false,
        },
        {
          id: 2,
          title: "hehe",
          subTitle: "hi2",
          imgSrc: piccc,
          link: "https://en.wikipedia.org/wiki/Lavander",
          selected: false,
        },
      ],
    };
  }

  handleCardClick = (id, card) => {
    let items = [...this.state.items];

    items[id].selected = items[id].selected ? false : true;

    items.forEach((item) => {
      if (item.id !== id) {
        item.selected = false;
      }
    });

    this.setState({
      items,
    });
  };

  makeItem = (item) => {
    return items.map((item) => {
      return (
        <Card
          item={item}
          onClick={(e) => this.handleCardClick(item.id, e)}
          key={item.id}
        />
      );
    });
  };

  render() {
    return <p>Carousel Works!</p>;
  }
}

export default Carousel;
